import styled from "styled-components";

export const SummaryContainer = styled.div`
  background-color: var(--gray-background-color);
  height: 35vw;
  color: var(--secondary-background-color);
  position: relative;
  max-width: 1200px;
  display: flex;
  align-items: center;
  justify-content: space;
  flex-wrap: wrap;

  .image-container {
    position: relative;
    padding: 1rem;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  > img {
    position: absolute;
    width: auto;
    height: 30vw;
  }

  #hexagono {
    width: 20rem;
    height: 11rem;
    /* background-image: url('./foto-justice.jpg');
    background-size: cover;
    background-position: center center;  */
    position: relative;
  }

  #hexagono:before {
    content: "";
    position: absolute;
    top: -2.5rem;
    left: 0;
    width: 0;
    height: 0;
    border-left: 10rem solid transparent;
    border-right: 10rem solid transparent;
    border-bottom: 5rem solid blue;
  }

  #hexagono:after {
    content: "";
    position: absolute;
    bottom: -5rem;
    left: 0;
    width: 0;
    height: 0;
    border-left: 10rem solid transparent;
    border-right: 10rem solid transparent;
    border-top: 5rem solid blue;
  }
`;
