import React, { ReactElement, useState, useCallback } from 'react';
import { ChevronLeft, ChevronRight } from 'react-feather';
import { CarouselContainer } from './styles';

type Props = {
  children: ReactElement[];
};

type BtnProps = {
  className: string;
  onClick: () => {};
}

const PrevArrow = (props: BtnProps) => {
  const { className, onClick } = props;

  return (
    <div className={className}>
      <button onClick={onClick}>
        <ChevronLeft size={36} />
      </button>
    </div>
  );
};

const NextArrow = (props: BtnProps) => {
  const { className, onClick } = props;

  return (
    <div className={className}>
      <button onClick={onClick}>
        <ChevronRight size={36} />
      </button>
    </div>
  );
};

export default function Carousel(props: Props) {
 const { children } = props;
 const [activeIndex, setActiveIndex] = useState(0);


 const updateImage = (newIndex: number) => {
  if (newIndex < 0) {
    newIndex = React.Children.count(children) - 1;
  } else if (newIndex >= React.Children.count(children) - 1) {
    newIndex = 0;
  }
  setActiveIndex(newIndex);
 };

//  if (!children) return null;

  return (
    <CarouselContainer>
      <div className='inner' style={{ transform: `translateX(-${activeIndex * 25}%)`}}>
        {React.Children.map(children, (child, _index) => {
          return React.cloneElement(child, { width: "33%"});
        })}
      </div>
      <div className='indicators'>
        <button disabled={activeIndex <= 0} onClick={() => updateImage(activeIndex - 1)
        }>
          prev
        </button>
        {/* {React.Children.map(children, (_child, index) => {
          return (
            <button onClick={() => updateImage(index)}>
              {index + 1}
            </button>
          )
        })} */}
        <button disabled={activeIndex === (React.Children.count(children) - 1)} onClick={() =>
          updateImage(activeIndex + 1)}>
          next
        </button>
      </div>
    </CarouselContainer>
  );
};
