import styled from "styled-components";

export const SummaryContainer = styled.div`
  background-color: var(--gray-background-color);
  height: 35vw;
  color: var(--secondary-background-color);
  position: relative;

  .container-hexagono {
    position: absolute;
    top: auto;
  }

  #hexagono {
    width: 300px;
    height: 55px;
    /* background-image: url('./foto-justice.jpg');
    background-size: cover;
    background-position: center center;  */
    position: relative;
  }

  #hexagono:before {
    content: "";
    position: absolute;
    top: -50%;
    left: 0;
    width: 0;
    height: 0;
    border-left: 100% solid transparent;
    border-right: 100% solid transparent;
    border-bottom: 50% solid blue;
  }

  #hexagono:after {
    content: "";
    position: absolute;
    bottom: -50%;
    left: 0;
    width: 0;
    height: 0;
    border-left: 100% solid transparent;
    border-right: 100% solid transparent;
    border-top: 50% solid blue;
  }
`;
