import styled from "styled-components";

export const CarouselContainer = styled.div`
  overflow: hidden;
  width: 100%;

  .inner {
    white-space: nowrap;
    transition: transform 0.3s;
  }
`;

export const CarouselItem = styled.div`
  display: inline-flex;
  align-items: center;
  justify-content: center;
  height: 200px;
  /* width: 100%; */
  background-color: green;
  color: #fff;
`;
