import styled from "styled-components";

export const CarouselContainer = styled.div`
  overflow: hidden;
  width: 100vw;

  .inner {
    white-space: nowrap;
    transition: transform 0.3s;
  }

  .indicators {
    display: flex;
    justify-content: center;
  }

  .indicators > button {
    margin: 5px;
    border-radius: 100%;
    height: 30px;
  }
`;

export const CarouselItem = styled.div`
  display: inline-flex;
  align-items: center;
  justify-content: center;
  height: 200px;
  width: 33%;
  background-color: green;
  color: #fff;
`;
